{
  "chartId": "chart_2",
  "dataSource": {
    "datasetId": 3,
    "queryType": "multi",
    "queryConfig": {
      "mainTable": "billing",
      "joins": [
        {
          "table": "departments",
          "type": "LEFT",
          "condition": "billing.department_id = departments.id"
        },
        {
          "table": "patients",
          "type": "LEFT",
          "condition": "billing.patient_id = patients.id"
        }
      ],
      "dimensions": [
        "departments.name as department",
        "DATE_FORMAT(billing.created_at, '%Y-%m') as month"
      ],
      "metrics": [
        "SUM(billing.amount) as total_revenue",
        "COUNT(DISTINCT patients.id) as patient_count",
        "AVG(billing.amount) as avg_revenue"
      ],
      "filters": [
        {
          "field": "billing.created_at",
          "operator": ">=",
          "value": "2024-01-01"
        },
        {
          "field": "billing.status",
          "operator": "=",
          "value": "paid"
        }
      ],
      "groupBy": ["departments.name", "DATE_FORMAT(billing.created_at, '%Y-%m')"],
      "having": [
        {
          "field": "SUM(billing.amount)",
          "operator": ">",
          "value": 10000
        }
      ],
      "orderBy": [
        {
          "field": "total_revenue",
          "direction": "desc"
        }
      ],
      "limit": 50
    }
  }
}

