  "dataSource": {
    "datasetId": 4,
    "queryType": "sql",
    "queryConfig": {
      "sql": "SELECT DATE(p.created_at) as date, COUNT(p.id) as patient_count, SUM(b.amount) as daily_revenue, AVG(p.age) as avg_age FROM patients p LEFT JOIN billing b ON p.id = b.patient_id WHERE p.created_at >= DATE_SUB(NOW(), INTERVAL 30 DAY) AND b.status = 'paid' GROUP BY DATE(p.created_at) ORDER BY date DESC LIMIT 30",
      "parameters": {
        "days_back": 30,
        "billing_status": "paid"
      }
    }
  }
}

